custom.scss:
//Custom Css

.mobile {

	@include breakpoint("medium") {
		display: none;
	}
}
.pc {
	display: none;

	@include breakpoint("medium") {
		display: inline-block;
	}
}

.pc-block {
	display: none;

	@include breakpoint("medium") {
		display: block;
	}
}

//global
.button-bg-white, .button-bg-red {
	display: inline-block;
	text-decoration: none;
	text-transform: uppercase;
	background: url("../img/button-bg-white.png") 0 50% no-repeat;
	background-size: contain;
	padding: 25px 10px 15px 40px;

}
.button-bg-red {
	background-image: url("../img/button-bg-red.png");
}


//featured-banner
.featured-banner {
	position: relative;
	margin-bottom: 35px;

    img {
    	width: 100%;
    }

    .featured-banner-inner {
    	background-color: stencilColor("color-blue");

    	@include breakpoint("small") {
    		position: absolute;
	    	top: 50%;
	    	left: 0;
	    	right: 0;
	    	transform: translateY(-50%);
	    	-webkit-transform: translateY(-50%);
	    	-moz-transform: translateY(-50%);
	    	background-color: transparent;

    	}
    }

    .featured-banner-content {
    	color: #fff;
    	padding: 30px 0;
    	text-align: left;

    	@include breakpoint("small") {
    		max-width: 630px; //590px + 2 * padding
    		padding: 20px;
    	}

    	@include breakpoint("medium") {
    		padding: 20px 50px;
    	}


    	h3 {
    		font-size: 24px;
    		color: #fff;
    		margin-top: 10px;
    		text-transform: uppercase;
    		font-weight: 700;
    		line-height: 1;

    		@include breakpoint("small") {
	    		font-size: 40px;
	    	}

	    	@include breakpoint("medium") {
	    		font-size: 50px;
	    	}
	    	@include breakpoint("large") {
	    		font-size: 67px;
	    		margin-bottom: 20px;
	    	}

    	}
    	p {
    		font-size: 14px;

    		@include breakpoint("small") {
	    		font-size: 16px;
	    	}

	    	@include breakpoint("medium") {
	    		font-size: 18px;
	    	}
	    	@include breakpoint("large") {
	    		font-size: 24px;
	    	}

    	}

    	a {
    		font-size: 16px;
    		color: #fff;

    		@include breakpoint("small") {
	    		font-size: 16px;
	    	}

	    	@include breakpoint("medium") {
	    		font-size: 18px;
	    	}
	    	@include breakpoint("large") {
	    		font-size: 21px;
	    	}
    	}

    }
}


//homepage banner
.checkout-banner {
	overflow: hidden;

}
.checkoutList {
	@include clearfix;
	list-style: none;
	margin: 0;
	padding: 0;
	margin: 0 -3px;

	li {
		padding: 0 3px;
		margin-bottom: 5px;
		width: 100%;

		@include breakpoint("small") {
			float: left;
			width: 50%;

		}
		@include breakpoint("medium") {
			width: 33.33%;
			
		}
	}

	.checkoutList-wrap {
		position: relative;

		img {
			width: 100%;
		}
	}

	.checkoutList-content {
		position: absolute;
		bottom: 50%;
		transform: translateY(50%);
		-webkit-transform: translateY(50%);
		-moz-transform: translateY(50%);
		left: 0;
		right: 0;
		color: #fff;
		padding: 10px 20px;
		text-align: center;

		@include breakpoint("large") {
			bottom: 60px;
			transform: none;
			-webkit-transform: none;
		    -moz-transform: none;
		}

		h3 {
			color: #fff;
			font-size: 20px;
			font-weight: 800;
			margin-top: 0;
			margin-bottom: 15px;
			line-height: 1;

			@include breakpoint("medium") {
				font-size: 34px;
			}
			@include breakpoint("large") {
				font-size: 43px;
			}
		}

		p {
		 	font-size: 14px;
		 	line-height: 1.3;
		 	margin-bottom: 20px;
		 	@include breakpoint("medium") {
				font-size: 16px;
			}
			@include breakpoint("large") {
				font-size: 19px;
				margin-bottom: 40px;
			}
		}

		a {
			font-size: 12px;
			font-weight: 600;
			color: #fff;
			@include breakpoint("medium") {
				font-size: 14px;
			}
			@include breakpoint("large") {
				font-size: 16px;
			}

		}


	}

}

.form-actions {
	.button +.forgot-password {
