}

//product compare css
.compare-product-section {
    display: none;
    position: fixed;
    bottom: 0;
    left: 0;
    right: 0;
    padding: 25px 20px;
    background-color: rgba(255, 255, 255, 0.35);
    text-align: center;
    z-index: 999;

    &:before {
    	display: block;
    	content: "";
    	position: absolute;
    	top: 0;
    	left: 0;
    	right: 0;
    	bottom: 0;
    	background-color: rgba(0, 0, 0, 0.75);

    }

    @include breakpoint("medium") {
    	&.show {
	        display: block;
	    }
    }

    .compare-product-list {
        list-style: none;
        margin: 0;
        padding: 0;
        display: inline-block;
        vertical-align: top;

    }
    .compare-product-item {
        padding: 0 12px;
        float: left;
    }
    .compare-item-inner {
        position: relative;
        height: 80px;
        width: 80px;
        background-color: #fff;
        background-size: contain;
        background-position: center;
        background-repeat: no-repeat;
    }

    .compare-item-delete {
        position: absolute;
        top: -8px;
        right: -8px;
        height: 20px;
        width: 20px;
        border-radius: 50%;
        background-color: #ac0000;
        cursor: pointer;
        padding: 3px;

        svg {
            fill: #fff;
            stroke: #fff;
        }

        &:hover {
            opacity: 0.8;
        }
    }

    .compare-link {
        margin: 20px 0 0 20px;
        padding: 10px 16px;
        font-size: 20px;
        vertical-align: top;

    }
}

.alert-window {
    position: fixed;
    top: 0;
    right: 0;
    left: 0;
    bottom: 0;
    opacity: 0;
    z-index: -999;
    background-color: transparent;
    transition: top .2s ease;
    -webkit-transition: top .2s ease;
    -moz-transition: top .2s ease;
    
    &.show {
        opacity: 1;
        z-index: 999;
        background-color: rgba(255,255,255, .45);
    }

    .alert-close {
        position: absolute;
        top: 0px;
        right: 0px;
        height: 30px;
        width: 30px;
        padding: 3px;
        //border: 1px solid #fff;
        cursor: pointer;

        svg {
            fill: #fff;
        }

        &:hover {
            opacity: 0.8;
        }
    }

    .alert-info {
        position: absolute;
        top: 50%;
        left: 50%;
        transform: translate(-50%, -50%);
        -webkit-transform: translate(-50%, -50%);
        -moz-transform: translate(-50%, -50%);
        color: #fff;
        padding: 50px;
        background-color: rgba(0,0,0, .8);
    }   
}

//manage
#manage{
	overflow: hidden;
	.side-menu{
		width: 20%;
		float: left;
		margin-right: 5%;
		ul{
			margin: 0;
			list-style-type: none;
		}
		.menu-list{
            li{
				font-size: 16px;
				line-height: 45px;
				padding-left:20px;
				border: 1px solid #dfdfdf;
				border-bottom: none;
				color: #666666;
				cursor: pointer;
				&:hover{
					color: #999;
				}
				a{
					text-decoration: none;
					display: block;
					color: #666;
				}
			}
			li:last-child{
				border-bottom: 1px solid #dfdfdf;
			}
			.child-list{
				display: none;
				li{
					border:none;
				}
				.ftBold{
					font-weight: bold;
				}
			}
			.item{
				.icon{
					transform: rotate(180deg);
					display: block;
					float:right;
					height: 45px;
					width: 45px;
					text-align: center;
					svg{
						width: 70%;
					}
				}
			}
			.is-open{
				.icon{
					transform: rotate(0);
				}
				.child-list{
					display: block;
				}
			}
		}
	}
	.content{
		width: 75%;
		float: left;
		display: none;
	}
	.form-actions{
		text-align: left;
		input[type='reset']{
			background: #999999;
			border-color: #999999;
		}
	}
	.elTitle{
		background: #CCCCCC;
		color: #FFFFFF;
		height: 40px;
		font-size: 20px;
		font-weight: bold;
		padding-left: 20px;
		line-height: 40px;
	}
}
.tbl{
